import React, { useState, useEffect } from 'react'; import BookCard from '../components/BookCard'; import SearchBar from '../components/SearchBar'; import LoadingSpinner from '../components/LoadingSpinner'; import { PencilIcon, TrashIcon } from '@heroicons/react/outline';

const API_BASE = process.env.REACT_APP_API_BASE || 'http://localhost:8080';

const AllBooksPage = () => { const [books, setBooks] = useState([]); const [filteredBooks, setFilteredBooks] = useState([]); const [sortBy, setSortBy] = useState('newest'); const [selectedCategory, setSelectedCategory] = useState('all'); const [loading, setLoading] = useState(true); const [currentPage, setCurrentPage] = useState(1); const [error, setError] = useState(null); const booksPerPage = 12;

useEffect(() => { const fetchBooks = async () => { try { setLoading(true); setError(null);

    const res = await fetch(`${API_BASE}/api/v1/books`);
    if (!res.ok) {
      throw new Error(`Failed to fetch books (${res.status})`);
    }

    const json = await res.json();

    // รองรับทั้งกรณี array ตรง และกรณีห่อใน data/items/books/results
    const list = Array.isArray(json)
      ? json
      : json.data || json.items || json.books || json.results || [];

    // normalize field ให้ไม่ undefined
    const normalized = list.map((b, idx) => ({
      ...b,
      id: b.id ?? b._id ?? b.bookId ?? idx + 1,
      title: b.title ?? b.name ?? '',
      author: b.author ?? b.writer ?? '',
      category: b.category ?? b.genre ?? 'อื่นๆ',
      price: Number(b.price ?? b.currentPrice ?? 0),
      reviews: Number(b.reviews ?? b.reviewCount ?? 0),
      coverImage: b.coverImage ?? b.imageUrl ?? b.image ?? b.cover ?? '',
      createdAt: b.createdAt ?? null,
    }));

    setBooks(normalized);
    setFilteredBooks(normalized);
  } catch (err) {
    console.error('Error fetching books:', err);
    setError(err.message || 'Fetch failed');
    setBooks([]);
    setFilteredBooks([]);
  } finally {
    setLoading(false);
  }
};

fetchBooks();

